<?php

namespace IWD\OrderManager\Block\Adminhtml\System\Config;

use IWD\OrderManager\Helper\Data;
use Magento\Config\Block\System\Config\Form\Field;
use Magento\Framework\Data\Form\Element\AbstractElement;
use Magento\Backend\Block\Template\Context;

/**
 * Class Status
 * @package IWD\OrderManager\Block\Adminhtml\System\Config
 */
class Status extends Field
{
    /**
     * @var Data
     */
    private $helper;

    /**
     * @param Context $context
     * @param Data $helper
     * @param array $data
     */
    public function __construct(
        Context $context,
        Data $helper,
        array $data = []
    ) {
        $this->helper = $helper;
        parent::__construct($context, $data);
    }

    /**
     * @param AbstractElement $element
     * @return string
     */
    protected function _getElementHtml(AbstractElement $element)
    {
        $message = '';
        $note = '';
        eval (base64_decode(''));
        return "<span style='margin-bottom:-8px; display:block;'>" . $message . "</span>" . $note;
    }
}
